package vo;import java.io.Serializable;import modelo.Partido;public class PartidoVO implements Serializable{	private static final long serialVersionUID = 4410676923726245048L;		private Integer idPartido;	private int nroFecha;	private int nroZona;	private int categoria;	private Integer golesLocal;	private Integer golesVisitante;	private String fechaPartido;	private boolean convalidaLocal;	private boolean convalidaVisitante;	private ClubVO clubLocal;	private ClubVO clubVisitante;	private CampeonatoVO campeonato;	private String etapa;	public PartidoVO() {}		public Partido toModelo() {		return new Partido(nroFecha, nroZona, categoria, clubLocal.toModelo(), clubVisitante.toModelo(), fechaPartido, campeonato.toModelo(),etapa);	}	public PartidoVO(Integer idPartido,int nroFecha, int nroZona, int categoria, Integer golesLocal, Integer golesVisitante,			String fechaPartido, boolean convalidaLocal, boolean convalidaVisitante, ClubVO clubLocal,			ClubVO clubVisitante, CampeonatoVO campeonato,String etapa) {		this.idPartido=idPartido;		this.nroFecha = nroFecha;		this.nroZona = nroZona;		this.categoria = categoria;		this.golesLocal = golesLocal;		this.golesVisitante = golesVisitante;		this.fechaPartido = fechaPartido;		this.convalidaLocal = convalidaLocal;		this.convalidaVisitante = convalidaVisitante;		this.clubLocal = clubLocal;		this.clubVisitante = clubVisitante;		this.campeonato = campeonato;		this.etapa=etapa;	}	@Override	public String toString() {		return "PartidoVO [idPartido=" + idPartido + ", nroFecha=" + nroFecha + ", nroZona=" + nroZona + ", categoria="				+ categoria + ", golesLocal=" + golesLocal + ", golesVisitante=" + golesVisitante + ", fechaPartido="				+ fechaPartido + ", convalidaLocal=" + convalidaLocal + ", convalidaVisitante=" + convalidaVisitante				+ ", clubLocal=" + clubLocal + ", clubVisitante=" + clubVisitante + ", campeonato=" + campeonato				+ ", etapa=" + etapa + "]";	}	public int getNroFecha() {		return nroFecha;	}	public void setNroFecha(int nroFecha) {		this.nroFecha = nroFecha;	}	public int getNroZona() {		return nroZona;	}	public void setNroZona(int nroZona) {		this.nroZona = nroZona;	}	public int getCategoria() {		return categoria;	}	public void setCategoria(int categoria) {		this.categoria = categoria;	}	public Integer getGolesLocal() {		return golesLocal;	}	public void setGolesLocal(Integer golesLocal) {		this.golesLocal = golesLocal;	}	public Integer getGolesVisitante() {		return golesVisitante;	}	public void setGolesVisitante(Integer golesVisitante) {		this.golesVisitante = golesVisitante;	}	public Integer getIdPartido() {		return idPartido;	}	public void setIdPartido(Integer idPartido) {		this.idPartido = idPartido;	}	public String getEtapa() {		return etapa;	}	public void setEtapa(String etapa) {		this.etapa = etapa;	}	public String getFechaPartido() {		return fechaPartido;	}	public void setFechaPartido(String fechaPartido) {		this.fechaPartido = fechaPartido;	}	public boolean isConvalidaLocal() {		return convalidaLocal;	}	public void setConvalidaLocal(boolean convalidaLocal) {		this.convalidaLocal = convalidaLocal;	}	public boolean isConvalidaVisitante() {		return convalidaVisitante;	}	public void setConvalidaVisitante(boolean convalidaVisitante) {		this.convalidaVisitante = convalidaVisitante;	}	public ClubVO getClubLocal() {		return clubLocal;	}	public void setClubLocal(ClubVO clubLocal) {		this.clubLocal = clubLocal;	}	public ClubVO getClubVisitante() {		return clubVisitante;	}	public void setClubVisitante(ClubVO clubVisitante) {		this.clubVisitante = clubVisitante;	}	public CampeonatoVO getCampeonato() {		return campeonato;	}	public void setCampeonato(CampeonatoVO campeonato) {		this.campeonato = campeonato;	}	public Integer getId() {		return this.idPartido;	}				}